FUNCTION_BLOCK Motor
VAR_INPUT
	MotorI:MotorI;
END_VAR
VAR_OUTPUT
	MotorO:MotorO;
END_VAR
VAR
	WaitTimer:TON;
	InternalSequenceDelay:time;
END_VAR


SetFault();
ResetFault();

METHOD ResetFault : BOOL
VAR_INPUT
	MotorI:MotorI;
END_VAR
VAR_OUTPUT
	MotorO:MotorO;
END_VAR
VAR
	WaitTimer:TON;
	InternalSequenceDelay:time;
	ResetDetect:r_trig;
END_VAR

ResetDetect(CLK:=MotorI.FaultReset);

IF ResetDetect.Q THEN
	MotorO.FaultStatus:=FALSE;
END_IF

METHOD SetFault : BOOL
VAR_INPUT
	MotorI:MotorI;
END_VAR
VAR_OUTPUT
	MotorO:MotorO;
END_VAR
VAR
	WaitTimer:TON;
	InternalSequenceDelay:time;
END_VAR

IF MotorI.Overload THEN
	MotorO.FaultStatus:=TRUE;
	TurnOff();
END_IF

