metadata.ground_truth:
METHOD SetWaitTime : BOOL
VAR_INPUT
	MotorI:MotorI;
	SequenceDelay:TIME;
END_VAR
VAR_OUTPUT
	MotorO:MotorO;
END_VAR
VAR
	WaitTimer:TON;
	InternalSequenceDelay:time;
END_VAR

InternalSequenceDelay:=SequenceDelay;

METHOD TurnOff : BOOL
VAR_INPUT
	MotorI:MotorI;
END_VAR
VAR_OUTPUT
	MotorO:MotorO;
END_VAR
VAR
	WaitTimer:TON;
	InternalSequenceDelay:time;
END_VAR

MotorO.Coil:=false;

METHOD TurnOn : BOOL
VAR_INPUT
	MotorI:MotorI;
END_VAR
VAR_OUTPUT
	MotorO:MotorO;
END_VAR
VAR
	WaitTimer:TON;
	InternalSequenceDelay:time;
END_VAR

IF MotorO.FaultStatus = FALSE THEN
	WaitTimer(IN:=TRUE , PT:=InternalSequenceDelay);
END_IF

IF WaitTimer.Q THEN
	MotorO.Coil:=TRUE;
	WaitTimer(IN:=FALSE);
END_IF
