/// Calculates an SE(3) frame from a screw axis
/// (a normalized |TwistVec|) and an angle.
FUNCTION SMC_Frame_FromScrewAxisAngle
VAR_IN_OUT
    /// The resulting frame.
    f : SM3M.SMC_Frame;
END_VAR
VAR_IN_OUT CONSTANT
    /// The screw axis.
    A : TwistVec;
END_VAR
VAR_INPUT
    /// The angle.
    theta : LREAL;
END_VAR
VAR
    wv : SM3M.SMC_VECTOR3D;
    w2v : SM3M.SMC_VECTOR3D;
    wt : SM3M.SMC_VECTOR3D;
END_VAR

SM3M.SMC_V3_Cross(wv, A.w, A.v);
SM3M.SMC_V3_Cross(w2v, A.w, wv);

SM3M.SMC_V3_Mult(f.vT, A.v, theta);

/// Calculates an SE(3) frame from a screw axis
/// (a normalized |TwistVec|) and an angle.
FUNCTION SMC_Frame_FromScrewAxisAngle
VAR_IN_OUT
    /// The resulting frame.
    f : SM3M.SMC_Frame;
END_VAR
VAR_IN_OUT CONSTANT
    /// The screw axis.
    A : TwistVec;
END_VAR
VAR_INPUT
    /// The angle.
    theta : LREAL;
END_VAR
VAR
    wv : SM3M.SMC_VECTOR3D;
    w2v : SM3M.SMC_VECTOR3D;
    wt : SM3M.SMC_VECTOR3D;
END_VAR

SM3M.SMC_V3_Cross(wv, A.w, A.v);
SM3M.SMC_V3_Cross(w2v, A.w, wv);

SM3M.SMC_V3_Mult(f.vT, A.v, theta);
SM3M.SMC_V3_MAdd(f.vT, f.vT, wv, (1 - COS(theta)));
SM3M.SMC_V3_MAdd(f.vT, f.vT, w2v, (theta - SIN(theta)));

SM3M.SMC_V3_Mult(wt, A.w, theta);
SM3M.SMC_Matrix3_FromAxisAngle(f.mR, wt);

FUNCTION SMC_OpenChainKinematics_SolveForward
VAR_IN_OUT
    fTCP : SM3M.SMC_Frame;
END_VAR
VAR_IN_OUT CONSTANT
    aS : ARRAY[0..SM3M.SMC_MAX_VEC_DIM-1] OF TwistVec;
    vJoints : SM3M.SMC_Vec;
    fTCP_init : SM3M.SMC_Frame;
END_VAR
VAR
    i : UDINT;
    fTmp : SM3M.SMC_Frame;
    afCur : ARRAY[0..1] OF SM3M.SMC_Frame;
END_VAR

i := TO_UDINT(vJoints.n);
afCur[i AND 1] := fTCP_init;
WHILE i > 0 DO
    SMC_Frame_FromScrewAxisAngle(fTmp, aS[i-1], vJoints.a[i-1]);
    SM3M.SMC_Frame_Mult(afCur[(i-1) AND 1], fTmp, afCur[i AND 1]);

    i := i - 1;
END_WHILE

fTCP := afCur[0];

