/// Calculates the generalized cross product
/// between two |TwistVec|s, both representing
/// generalized velocities. The result is a
/// generalized acceleration.
FUNCTION Twist_Bracket_Twist
VAR_IN_OUT
    /// The resulting acceleration.
    V_dst : TwistVec;
END_VAR
VAR_IN_OUT CONSTANT
    /// The first velocity.
    V_lhs : TwistVec;
    /// The second velocity.
    V_rhs : TwistVec;
END_VAR
VAR
    v0 : SM3M.SMC_VECTOR3D;
    v1 : SM3M.SMC_VECTOR3D;
END_VAR

SM3M.SMC_V3_Cross(v0, V_lhs.v, V_rhs.w);
SM3M.SMC_V3_Cross(v1, V_lhs.w, V_rhs.v);
SM3M.SMC_V3_Cross(V_dst.w, V_lhs.w, V_rhs.w);
SM3M.SMC_V3_Add(V_dst.v, v0, v1);

/// Calculates the generalized cross product
/// between a |TwistVec|, representing a generalized
/// velocity, and a |WrenchVec|, representing a
/// generalized momentum, resulting in a generalized
/// force.
FUNCTION Twist_Bracket_Wrench
VAR_IN_OUT
    /// The resulting force.
    F_dst : WrenchVec;
END_VAR
VAR_IN_OUT CONSTANT
    /// The velocity.
    V_src : TwistVec;
    /// The momentum.
    F_src : WrenchVec;
END_VAR
VAR
    m0 : SM3M.SMC_VECTOR3D;
    m1 : SM3M.SMC_VECTOR3D;
END_VAR

SM3M.SMC_V3_Cross(m0, F_src.m, V_src.w);
SM3M.SMC_V3_Cross(m1, F_src.f, V_src.v);
SM3M.SMC_V3_Cross(F_dst.f, F_src.f, V_src.w);
SM3M.SMC_V3_Add(F_dst.m, m0, m1);

FUNCTION WrenchVec_Add
VAR_IN_OUT
    F_dst : WrenchVec;
END_VAR
VAR_IN_OUT CONSTANT
    F_lhs : WrenchVec;
    F_rhs : WrenchVec;
END_VAR

SM3M.SMC_V3_Add(F_dst.m, F_lhs.m, F_rhs.m);
SM3M.SMC_V3_Add(F_dst.f, F_lhs.f, F_rhs.f);

FUNCTION WrenchVec_Sub
VAR_IN_OUT
    V_dst : WrenchVec;
