		stat := gDev.grd[idx].state;
	2:
		extendVal := gDev.sky[idx].sensor.extendVal;
		retractVal := gDev.sky[idx].sensor.retractVal;
		nowVal := gDev.sky[idx].sensor.position;
		stat := gDev.sky[idx].state;
END_CASE

IF nowVal > 1 THEN
