METHOD pidExecute
VAR_INPUT
END_VAR
VAR
	vector 		:TargetDirect;
END_VAR

FOR gi := 1 TO 10 DO
	IF gDev.grd[gi].ctrl.isError THEN
		CONTINUE;
	END_IF
	cmdExecutor(level := 1, idx := gi, percent := grdPid[gi].res);
END_FOR


