{attribute 'qualified_only'}
{attribute 'strict'}
TYPE DevStatus :
(
	unknown 	:= 0,

	runing		:= 1,

	extended	:= 2,

	retracted	:= 3,

	error		:= 4
);
END_TYPE

TYPE FeedForward :
STRUCT

	pstThreshold	:REAL;	//正向设定阈值	
	pstKv			:REAL;
	pstKa			:REAL;
	pstKaa			:REAL;
	negThreshold	:REAL;	//反向设定阈值
	negKv			:REAL;
	negKa			:REAL;
	negKaa			:REAL;



//	pstFactor		:REAL;	//正向设定速度系数因子
//	negFactor		:REAL;	//反向设定速度系数因子


END_STRUCT
END_TYPE

TYPE PidParam :
STRUCT
	Kp			:REAL;
	Ki			:REAL;
	Kd			:REAL;
	Set_Point	:REAL;
	Max_Val		:REAL;
	Max_ACC		:REAL;
	Act_Val		:REAL;
	Err			:REAL;
	OUT			:REAL;
END_STRUCT
END_TYPE

{attribute 'qualified_only'}
{attribute 'strict'}
TYPE TargetDirect :
(
	extend 	:= 1,	//伸出

	retract := 2,	//缩回

	stop	:= 3	//停止
);
END_TYPE

TYPE UiPid :
STRUCT
	pos				:PidParam;			//位置pid参数
	spd				:PidParam;			//速度pid参数
	ff				:FeedForward;		//前馈参数

	level			:INT;				//1->地轨	2->天轨
	idx				:INT;				//挡板索引编号

	devSetPos		:REAL;				//设备中的设定位置点(用于观测)
	executeVal		:REAL;				//执行值(-100.0~+100.0)


	posBtn			:BOOL;			//位置控制按钮，按下实时更新交互参数
	rstPidBtn		:BOOL;			//reset pid param button重置pid参数按钮
	pwrPercent		:REAL;			//比例阀百分比 power-percent 0-100	




	extendBtn		:BOOL;
	retractBtn		:BOOL;
	stopBtn			:BOOL;




(*	posErr			:REAL;
	posOut			:REAL;
	spdErr			:REAL;

