VAR
	sDescription : STRING;
	rValue : REAL;
END_VAR

METHOD CreateObject : BOOL
VAR
	bSuccess : BOOL;
END_VAR

bSuccess := FALSE;

IF sDescription = '' THEN
	sDescription := 'Default Description';
END_IF;

IF rValue = 0.0 THEN
	rValue := 1.0;
END_IF;

bSuccess := TRUE;

CreateObject := bSuccess;
