VAR
	refAirConditioning : REFERENCE TO FB_AirConditioning;
END_VAR

METHOD Execute : BOOL
VAR
	isValidRef : BOOL;
END_VAR

isValidRef := __ISVALIDREF(refAirConditioning);

IF isValidRef THEN
	refAirConditioning.Activate();
	refAirConditioning.SetTemperature(20.0);
	Execute := TRUE;
ELSE
	Execute := FALSE;
END_IF
