METHOD CreateObject : BOOL
VAR
	tempDescription : STRING;
	tempValue : REAL;
END_VAR

tempDescription := 'Basic Object';
tempValue := 0.0;

sDescription := tempDescription;
rValue := tempValue;

CreateObject := TRUE;
RETURN;
