VAR
    refToRealSubject : POINTER TO Realsubject;
    iTempPassword : INT;
END_VAR
VAR_STAT
    iRealPassword : INT := 1234;
END_VAR

METHOD fb_init
VAR_INPUT
    bInitRetains : BOOL;
    bInCopyCode : BOOL;
    ptrToRealSubject : POINTER TO Realsubject;
    iTempPassword : INT;
    iSetValue : INT;
    bReadWrite : BOOL;
END_VAR
VAR_OUTPUT
END_VAR
VAR
    refToRealSubject : POINTER TO Realsubject;
    iTempPassword : INT;
END_VAR
VAR_STAT
END_VAR

IF ptrToRealSubject <> 0 THEN
    refToRealSubject := ptrToRealSubject;
END_IF

IF bReadWrite THEN
    IF iTempPassword = iRealPassword THEN
        refToRealSubject^.iProp := iSetValue;
    END_IF
ELSE
    IF iTempPassword = iRealPassword THEN
        RETURN refToRealSubject^.iProp;
    ELSE
        RETURN -1; // Invalid password
    END_IF
END_IF
