VAR
	elevatorDirection : STRING; // 电梯运动方向
END_VAR

IF nTargetFloor > nCurrentFloor THEN
	// 目标楼层高于当前楼层，电梯上升
	elevatorDirection := 'UP';
ELSIF nTargetFloor < nCurrentFloor THEN
	// 目标楼层低于当前楼层，电梯下降
	elevatorDirection := 'DOWN';
ELSE
	// 目标楼层等于当前楼层，电梯保持静止
	elevatorDirection := 'STOP';
END_IF;

RETURN elevatorDirection;
