FUNCTION_BLOCK FB_Wrapper
VAR_INPUT
	in:BOOL;
END_VAR
VAR_OUTPUT
	out:BOOL;
END_VAR
VAR
	fb_teste:array [1..3] of FB_Teste;
	index: INT;
END_VAR

Map();


fb_teste[1].SetTempo(delay:=T#4S);
fb_teste[2].SetTempo(delay:=T#1S);
fb_teste[3].SetTempo(delay:=T#2S);

FOR index := 1 TO 3 DO
IF in THEN
	fb_teste[index].mLiga();
ELSE
	fb_teste[index].mDesliga();
END_IF

END_FOR

FUNCTION_BLOCK FB_Wrapper
VAR_INPUT
	in:BOOL;
END_VAR
VAR_OUTPUT
	out:BOOL;
END_VAR
VAR
	fb_teste:array [1..3] of FB_Teste;
	index: INT;
END_VAR

Map();


fb_teste[1].SetTempo(delay:=T#4S);
fb_teste[2].SetTempo(delay:=T#1S);
fb_teste[3].SetTempo(delay:=T#2S);

FOR index := 1 TO 3 DO
IF in THEN
	fb_teste[index].mLiga();
ELSE
	fb_teste[index].mDesliga();
END_IF

FUNCTION_BLOCK FB_Teste
VAR_INPUT
	entrada:BOOL;
END_VAR
VAR_OUTPUT
	saida:BOOL;
END_VAR
VAR
	Timer:ton;
	tempo:TIME;
END_VAR

PROGRAM PLC_PRG
VAR
	Wrapper:FB_Wrapper;
	input: BOOL;
	output: BOOL;
END_VAR

Wrapper(in:=input , out=>output );

METHOD Map : BOOL
VAR
	index:int;
    fb_teste:array [1..3] of FB_Teste;
	index: INT;
END_VAR
VAR_INPUT
	in:BOOL;
END_VAR
VAR_OUTPUT
	out:BOOL;
END_VAR

FOR index:= 1 TO 3 DO

fb_teste[index].entrada:=in;
out:=fb_teste[index].saida;

END_FOR

METHOD mDesliga : BOOL
VAR_INPUT
	entrada:BOOL;
END_VAR
VAR_OUTPUT
	saida:BOOL;
END_VAR
VAR
	Timer:ton;
	tempo:TIME;
END_VAR

saida:=FALSE;
Timer(IN:=false);

METHOD mLiga : BOOL
VAR_INPUT
	entrada:BOOL;
END_VAR
VAR_OUTPUT
	saida:BOOL;
END_VAR
VAR
	Timer:ton;
	tempo:TIME;
END_VAR

Timer(IN:=TRUE , PT:=tempo);

IF Timer.Q THEN
	saida:=TRUE;
    Timer(IN:=FALSE);
END_IF

METHOD SetTempo : BOOL
VAR_INPUT
	delay:TIME;
	entrada:BOOL;
END_VAR
VAR_OUTPUT
	saida:BOOL;
END_VAR
VAR
	Timer:ton;
	tempo:TIME;
END_VAR

tempo:=delay;
