	IF fbPowerX.Error THEN
		sInternalError := 'X Axis Error';
	ELSIF fbPowerY.Error THEN
		sInternalError := 'Y Axis Error';
	ELSIF fbPowerZ.Error THEN
		sInternalError := 'Z Axis Error';
	END_IF;
	sErrorString := sInternalError;
ELSE
	bError := FALSE;
	sErrorString := '';
