RETURN TRUE;

METHOD PUBLIC SetCommand : BOOL
VAR_INPUT
	nPosition		: INT;
	iCommand		: I_Command;
END_VAR

IF ((nPosition >= 1) AND (nPosition <= 8) AND (iCommand <> 0)) THEN
	aiCommand[nPosition] := iCommand;
	RETURN TRUE;
ELSE
	RETURN FALSE;
END_IF

