			// 设置电梯运行方向
			F_SetElevatorDirection(Tar_Dir.IsInWhichFloor, i);
			Tar_Dir.IsFree := FALSE;
		END_IF
	END_IF
END_FOR

FOR i := 2 TO MaxNumFloor DO//第2、3、4楼楼层中（电梯外面）向下的按钮
	fbFloorDownLedRtrig[i](CLK := QFloorDownLed[i]);
	IF fbFloorDownLedRtrig[i].Q THEN
		Tar_Dir.IsStopFloor[i] := TRUE;
		Tar_Dir.IsDownStop[i] := TRUE;
		IF (Tar_Dir.IsFree = TRUE) THEN
			// 设置电梯运行方向
			F_SetElevatorDirection(Tar_Dir.IsInWhichFloor, i);
			Tar_Dir.IsFree := FALSE;
		END_IF
	END_IF
END_FOR

FOR i := 1 TO MaxNumFloor DO//电梯内的“1、2、3、4”的按钮
	fbGoFloorLedRtrig[i](CLK := QGoFloorLed[i]);
	IF fbGoFloorLedRtrig[i].Q THEN
		IF (Tar_Dir.IsStopFloor[i] <> TRUE) THEN
			Tar_Dir.IsStopFloor[i] := TRUE;
		END_IF
		IF i >= Tar_Dir.IsInWhichFloor THEN
			IF i < MaxNumFloor THEN
				Tar_Dir.IsUpStop[i] := TRUE;
			ELSE
				Tar_Dir.IsDownStop[i] := TRUE;
			END_IF
			IF Tar_Dir.IsFree THEN
				// 设置电梯运行方向（向上）
				F_SetElevatorDirection(Tar_Dir.IsInWhichFloor, i);
				Tar_Dir.IsFree := FALSE;
			END_IF
		ELSE
			IF i > 1 THEN
				Tar_Dir.IsDownStop[i] := TRUE;
			ELSE
				Tar_Dir.IsUpStop[i] := TRUE;
			END_IF
			IF Tar_Dir.IsFree THEN
				// 设置电梯运行方向（向下）
				F_SetElevatorDirection(Tar_Dir.IsInWhichFloor, i);
				Tar_Dir.IsFree := FALSE;
			END_IF

	END_IF
END_FOR

//各楼层电梯外面选择目标楼层，不可取消                            ///哪层被按下了哪层亮
FOR i := 1 TO MaxNumFloor - 1 DO //1、2、3楼的上升按钮
	fbFloorUpRtrig[i](CLK := IFloorUp[i]);
	IF fbFloorUpRtrig[i].Q AND NOT Tar_Dir.IsBlock THEN
		QFloorUpLed[i] := TRUE;
	END_IF
END_FOR
FOR i := 2 TO MaxNumFloor DO //2、3、4楼的下降按钮
	fbFloorDownRtrig[i](CLK := IFloorDown[i]);
	IF fbFloorDownRtrig[i].Q AND NOT Tar_Dir.IsBlock THEN
		QFloorDownLed[i] := TRUE;
	END_IF
END_FOR

UpdateTarget();      //更新数据结构Target_Direction中的指令状态信息

//由方向和目标楼层决定电梯的动作
IF Tar_Dir.IsPositive AND NOT bStopping THEN
	FOR i := Tar_Dir.IsInWhichFloor TO MaxNumFloor DO
		IF Tar_Dir.IsStopFloor[i] THEN   //从当前层到最高层是否有目标楼层（电梯要停止的楼层）
			QMotorPositiveRun := TRUE;//电梯上行
			EXIT;
		END_IF
	END_FOR
END_IF

IF Tar_Dir.IsNegative AND not bStopping THEN
	FOR i := 1 TO Tar_Dir.IsInWhichFloor DO
		IF Tar_Dir.IsStopFloor[i] THEN  //从最底层到当前层是否有目标楼层（电梯要停止的楼层）
			QMotorNegativeRun := TRUE;//电梯下行
			EXIT;
		END_IF
	END_FOR
END_IF

//根据电梯碰到楼层的到位开关决定电梯的动作
FOR i := 1 TO MaxNumFloor DO 
	//fbIsFloorRtrig1[i](CLK := IIsFloor[i]);
	fbIsStopFloorRtrig[i](CLK := Tar_Dir.IsStopFloor[i]);
	IF IIsFloor[i] AND fbIsStopFloorRtrig[i].Q THEN         ///电梯本来已经在目标层  电梯本来停在i层而且i层就是目标楼层（电梯要停止的楼层）
		bArrive := TRUE;
	ELSE
		bArrive := FALSE;
	END_IF

	IF fbIsFloorRtrig[i].Q AND Tar_Dir.IsStopFloor[i] THEN ///电梯运动到目标层   i层是目标楼层而且电梯运动到了该层
		bTrigArrive := TRUE;

FOR i := 1 TO MaxNumFloor DO
	fbIsFloorFtrig[i](CLK := IIsFloor[i]);
	IF fbIsFloorFtrig[i].Q THEN
		bTiming := TRUE;
		isInWhichFloor := i;
		EXIT;
	END_IF
END_FOR

fbTon3s(IN := bTiming,PT := T#3S);
IF fbTon3s.Q THEN
	bTiming := FALSE;
	IF QMotorPositiveRun THEN
		IIsFloor[isInWhichFloor + 1] := TRUE;
	END_IF
	IF QMotorNegativeRun THEN
		IIsFloor[isInWhichFloor - 1] := TRUE;
	END_IF
END_IF

//电梯门1s开、关到位
fbOpenRtrig(CLK := QOpen);
fbCloseRtrig(CLK := QClose);
IF fbOpenRtrig.Q THEN
	bTimingOpen := TRUE;
	bTimingClose := FALSE;
	IIsClose := FALSE;
END_IF
IF fbCloseRtrig.Q THEN
	bTimingClose := TRUE;
	bTimingOpen := FALSE;
	IIsOpen := FALSE;
END_IF

fbTonOpen(IN := bTimingOpen,PT := T#1S);
IF fbTonOpen.Q THEN
	bTimingOpen := FALSE;
	IIsOpen :=TRUE;
END_IF

fbTonClose(IN := bTimingClose,PT := T#1S);
IF fbTonClose.Q THEN
	bTimingClose := FALSE;
	IIsClose :=TRUE;
END_IF

bHideOpen := bTimingClose OR (NOT bTimingClose AND NOT bTimingOpen);         //隐藏电梯开门箭头
bHideClose := bTimingOpen OR (NOT bTimingClose AND NOT bTimingOpen);           //隐藏电梯关门箭头

metadata.ground_truth:
FUNCTION F_SetElevatorDirection
VAR_INPUT
	nCurrentFloor	:	USINT;		//当前楼层
	nTargetFloor	:	USINT;		//目标楼层
END_VAR

// 根据当前楼层和目标楼层设定电梯运行方向
IF nTargetFloor > nCurrentFloor THEN
	// 目标楼层高于当前楼层，电梯上升
	Tar_Dir.IsPositive := TRUE;
	Tar_Dir.IsNegative := FALSE;
ELSIF nTargetFloor < nCurrentFloor THEN
	// 目标楼层低于当前楼层，电梯下降
	Tar_Dir.IsPositive := FALSE;
	Tar_Dir.IsNegative := TRUE;
END_IF
// 如果目标楼层等于当前楼层，不改变方向
